mapper.select:

    select  distinct

    from tbl_sp_supplier s left join tbl_merchant_supplier_expand e on s.supplier_code=e.merchant_code
    LEFT JOIN tbl_sp_supplier_contract d ON s.id = d.supplier_id and d.renew_flag IN ('1', '2')

    left join tbl_supplier_yg_contact c on  c.user_id = e.yg_contact_user_id 





		order by e.${example.orderBy} 


		${example.sequence}



		order by s.update_date desc

	limit #{query.offset},#{query.pageSize}

