mapper.insert:

insert into tbl_merchant_center_operation_log (id, login_name, operated, 
  login_host, login_address, operation_type, 
  operation_menu, operation_order, operation_num, 
  type, merchant_code, operation_notes
  )
values (#{id,jdbcType=VARCHAR}, #{loginName,jdbcType=VARCHAR}, #{operated,jdbcType=TIMESTAMP}, 
  #{loginHost,jdbcType=VARCHAR}, #{loginAddress,jdbcType=VARCHAR}, #{operationType,jdbcType=VARCHAR}, 
  #{operationMenu,jdbcType=VARCHAR}, #{operationOrder,jdbcType=VARCHAR}, #{operationNum,jdbcType=VARCHAR}, 
  #{type,jdbcType=INTEGER}, #{merchantCode,jdbcType=VARCHAR}, #{operationNotes,jdbcType=LONGVARCHAR}
  )

mapper.select:
    select t.login_name,t.operated,t.login_host,t.login_address 	
     from tbl_merchant_center_operation_log t
	 where t.type = 1 and t.login_name in 

			#{loginName} 

	   order by t.operated desc limit #{query.offset}, #{query.pageSize}
